  //         return {
  //           id: movieData.episode_id,
  //           title: movieData.title,
  //           openingText: movieData.opening_crawl,
  //           releaseDate: movieData.release_date,
  //         };
  //       });
  //       setMovie(transformedData);
  //     });
  // };

  //Using asynchronous


 const fetchMovieHandler=useCallback( async() =>{
    setIsError(null);
    setIsLoading(true);
    try {


      const response = await fetch("https://swapi.dev/api/films/");
      const response2=await fetch("https://movie-adder-http-default-rtdb.firebaseio.com/movies.json")

      if(!response.ok){
        throw new Error("Something went Wrong!!");
      }
      const data = await response.json(); //This line trandform the json into javascript object
      const data2 = await response2.json(); //This line trandform the json into javascript object

      const loadedMovies=[];
      for(const key in data2){
        loadedMovies.push({
          id:key,
          title:data[key].title,
          open
        })


      }



      const transformedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovie(transformedData);

    } catch (error) {
      setIsError(error.message);
    }
    setIsLoading(false);
  },[]);
  useEffect(()=>{
    fetchMovieHandler();

  },[fetchMovieHandler]);

  const addMovieHandler= async (movie)=>{
    const response=await fetch("https://movie-adder-http-default-rtdb.firebaseio.com/movies.json",{
      method:'POST',
      body:JSON.stringify(movie),
      headers:{
        'Content-Type':'al=pplication/json'
      }
    })
    const data=await response.json();
    console.log(data);

  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler}></AddMovie>
      </section>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={movies} />}
        {isLoading && <p>Loading...</p>}
        {!isLoading && isError && <p>{isError}</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
